mapper.select:
select

from order_class
where 1=1

  and code = #{code,jdbcType=VARCHAR}


  and name like  concat('%',#{name},'%')


  and time_interval_total = #{timeIntervalTotal,jdbcType=INTEGER}


  and start_time = #{startTime,jdbcType=TIMESTAMP}


  and end_time = #{endTime,jdbcType=TIMESTAMP}

mapper.insert:
insert into order_class (id, code, name, 
  time_interval_total, start_time, end_time
  )
values (#{id,jdbcType=INTEGER}, #{code,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{timeIntervalTotal,jdbcType=INTEGER}, #{startTime,jdbcType=TIMESTAMP}, #{endTime,jdbcType=TIMESTAMP}
  )
